      'zh-cn': '<p>520%520%</p><p><code>grid-template-columns: 20% 20% 20% 20% 20%;</code><code>grid-template-rows: 20% 20% 20% 20% 20%;</code>5520%</p><p><code>grid-template-columns</code>50%</p>',
